props.globals.initNode(lightsPath ~ "beacon-state/state", 0, "BOOL");
var beacon_cycle = 1.0 + rand()*0.05 -0.025;
var beacon_phase = 1; 
var beacon_timer = maketimer(beacon_cycle, 
	func {
		if(getprop(mpPath ~ "sim/multiplay/generic/int[1]") and (beacon_phase == 1)) {
			setprop(lightsPath ~ "beacon-state/state", 1);
			beacon_phase = 0;
			beacon_timer.restart(0.0);
		}
		else {
			setprop(lightsPath ~ "beacon-state/state", 0);
			beacon_phase = 1;
			beacon_timer.restart(beacon_cycle);
		}
	}
);

beacon_timer.start();

